import React, {useState} from "react";
import SignInForm from "./pages/user/SignInForm";
import SignUpForm from "./pages/user/SignUpForm";

const Login = () => {

  const [view, setView] = useState({
    signIn: false,
    signUp: false
  })

  return (
    <main role="main">
      <section className="jumbotron text-center">
        <div className="container">
          <h1 className="jumbotron-heading">Movie</h1>
          <p className="lead text-muted">
            리뷰하고 싶은 영화를 추가하고, 별점을 주세요.
            <br />
            또한 삭제, 수정이 가능합니다.
          </p>
          <p>
            <button className="btn btn-primary my-2 m-1">로그인</button>
            <button className="btn btn-secondary my-2 m-1">회원가입</button>
          </p>
        </div>
      </section>
      {
        view.signIn ? (<SignInForm />) : (<></>)
      }
      {
        view.signUp ? (<SignUpForm />) : (<></>)
      }
    </main>
  );
};

export default Login;
